// Watch
var fs = require('fs');
function watch(dir, cb){
    cb = cb || function(){};
    dir = dir || __dirname;
    if (typeof dir == 'string')
    dir = [dir];
    dir.forEach(function(d){
        fs.realpath(d, function(e, d){
            if (!e) {
            fs.watch(d, function(e, f){
                cb(d, f, e);
            }); }
        });
    });
}

// Export
module.exports = watch;
