function taskTitle(txt) { console.log("\x1b[33m== "+txt+" ==\n\x1b[0m"); }
function taskDesc(txt) { console.log("\x1b[36m- "+txt+"\x1b[0m"); }
function taskPass() { console.log("\x1b[32m  PASS ✔️ \x1b[0m"); }
function taskFail() { console.log('\x1b[31m  FAIL ✖️ \x1b[0m'); }
function taskErr(e) { console.log('\x1b[35m  '+e.message+'\x1b[0m'); }
function taskEnd() { console.log('\x1b[33m\n--Done. \x1b[0m'); }

var mod = require('./fread');

// Exec tests //////////////////////////////////////////////////////////
taskTitle('FRead');

taskDesc('Read file.');
try {
    console.log('  file: '+__filename);
    mod(__filename);
    mod(__filename, function(){});
    taskPass();
} catch (e) { taskFail(); taskErr(e); }

taskDesc('Missing file should err');
try {
    console.log('  file: '+__dirname+'/missing.txt');
    mod('missing.txt');
    taskFail();
} catch (e) {
    taskPass();
}

// End
taskEnd();